export const calculator=t=>{if(!t||typeof t!="object")return console.warn("Invalid loan details provided."),!1;const e=t.tenure*12,o=t.roi*.01/12;return{monthlyLoanTenure:e,roi:o,loanDetails:t,result(){return p(this)}}};const s=t=>{const{loanDetails:e}=t,o=e.foir*.01;let n=0;if(document.querySelector("#calculator-product-type").value=="pl")return n=m(e.income,t.monthlyLoanTenure,e.otherLoan,t.roi),{result:n};const i=y(e.income,e.otherLoan,o);if(i<=0)return 0;const u=i,c=t.monthlyLoanTenure,l=t.roi;return n=Math.round(u*(1-1/(1+l)**c)/l),{result:n}},a=t=>{const e=t.loanDetails.loanAmt,o=t.monthlyLoanTenure,n=t.roi;let r=e*n*(1+n)**o/((1+n)**o-1);r=Math.round(r);const i=r*o-e;return{result:r,interestAmt:i,principalAmt:e}};function p(t){const{calType:e}=t.loanDetails;if(e)return e=="emi"?a(t):s(t)}function y(t,e,o){let n=document.querySelector("#calculator-product-type").value;const r=document.querySelector(".overlayDiv.show .homeloancalculator");r&&(n=r.querySelector("#calculator-product-type").value);let i=(t-e)*o;return n=="hl"&&(i=t*o-e),i}const m=(t,e,o,n)=>{const r=(t-o)*.5;let u=1/(1+n)**e;return u=r*(1-u)/n,u=Math.round(u),u<=0?0:u};
